/* TextForm Styles */
.text-form-container {
    padding: 2rem;
    border-radius: 10px;
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1); /* Card effect */
    background: #f8f9fa; /* Light background */
  }
  
  .text-form-heading {
    font-size: 1.75rem;
    color: #007bff;
    margin-bottom: 1rem;
  }
  
  .text-form-input {
    border: 1px solid #ced4da;
    border-radius: 5px;
    padding: 0.5rem 1rem;
    width: 100%;
    box-sizing: border-box;
    transition: border-color 0.3s ease;
  }
  
  .text-form-input:focus {
    border-color: #007bff;
    outline: none;
  }
  
  .text-form-button {
    background-color: #007bff;
    border: none;
    color: white;
    padding: 0.75rem 1.5rem;
    border-radius: 5px;
    font-size: 1rem;
    cursor: pointer;
    transition: background-color 0.3s ease;
  }
  
  .text-form-button:hover {
    background-color: #0056b3;
  }
